capteurs = [10.744964702168431, 3.356672123620143, 83.92700773184632, 97.46078570327255, 19.792995491301923, 99.96635269754944, 42.12528145697245, 47.09181147646758 ] # Liste des capteurs

plt.figure(figsize=(8, 8)) # Création d'une figure de taille 8x8 pouces

for x, y in cibles: # Pour chaque cible
    plt.plot(x, y, 'bo') # On trace un point bleu pour la cible

for i in range(0, len(capteurs), 2): # Pour chaque capteur
    plt.plot(capteurs[i], capteurs[i+1], 'rx', markersize=10) # On trace une croix rouge pour le capteur
    circle = plt.Circle((capteurs[i], capteurs[i+1]), 10, color='red', fill=False) # On trace un cercle rouge de rayon 10 autour du capteur
    plt.gca().add_artist(circle) # On ajoute le cercle à la figure

plt.xlim(0, 100) # On limite les abscisses entre 0 et 100
plt.ylim(0, 100) # On limite les ordonnées entre 0 et 100
plt.xlabel('X') # On nomme l'axe des abscisses
plt.ylabel('Y') # On nomme l'axe des ordonnées
plt.title('Cibles et Capteurs') # On donne un titre à la figure

plt.show() # On affiche la figure
